      out.write("<!-- 장바구니 쿠키 처리 -->\n");
      out.write("<script>\n");
      out.write("$(document).ready(function() {\n");
      out.write("    // 장바구니 담기 버튼 클릭\n");
      out.write("    $(document).on(\"click\", \"div.into_cart img\", function (e) {\n");
      out.write("        // 세션이 있으면 기존 addCart 함수가 처리하도록 반환\n");
      out.write("        if (window.hasProductCountSession) {\n");
      out.write("            return;\n");
      out.write("        }\n");
      out.write("        \n");
      out.write("        // 세션이 없으면 기본 이벤트와 addCart 실행 중지\n");
      out.write("        e.preventDefault();\n");
      out.write("        e.stopPropagation();\n");
      out.write("\n");
      out.write("        const pdtId = $(this).closest('.into_cart').data(\"pdtid\");\n");
      out.write("        let basketItems = [];\n");
      out.write("        \n");
      out.write("        // 기존 장바구니 쿠키 확인\n");
      out.write("        const cookie = document.cookie\n");
      out.write("            .split('; ')\n");
      out.write("            .find(row => row.startsWith('basketItems='));\n");
      out.write("        if (cookie) {\n");
      out.write("            try {\n");
      out.write("                basketItems = JSON.parse(decodeURIComponent(cookie.split('=')[1]));\n");
      out.write("            } catch (e) {\n");
      out.write("                console.error(\"쿠키 파싱 에러:\", e);\n");
      out.write("                basketItems = [];\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // 동일 상품 확인 (문자열로 비교)\n");
      out.write("        const existingItem = basketItems.find(item => item.id === pdtId.toString());\n");
      out.write("        if (existingItem) {\n");
      out.write("            if (confirm(\"장바구니에 동일한 상품이 있습니다. 수량을 추가하시겠습니까?\")) {\n");
      out.write("                existingItem.quantity += 1;\n");
      out.write("                alert(\"상품 수량이 증가했습니다.\");\n");
      out.write("            }\n");
      out.write("        } else {\n");
      out.write("            // 새 상품 추가\n");
      out.write("            basketItems.push({\n");
      out.write("                id: pdtId.toString(),\n");
      out.write("                quantity: 1\n");
      out.write("            });\n");
      out.write("            \n");
      out.write("            // 장바구니 카운트 업데이트\n");
