      out.write("            $(\".EC-Layout-Basket-count\").text(currentCount + 1);\n");
      out.write("            \n");
      out.write("            alert(\"장바구니에 상품이 추가되었습니다.\");\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // 쿠키에 저장\n");
      out.write("        document.cookie = \"basketItems=\" + \n");
      out.write("            encodeURIComponent(JSON.stringify(basketItems)) + \n");
      out.write("            \"; path=/\";\n");
      out.write("    });\n");
      out.write("});\n");
